// ==UserScript==
// @name         Copy MarkDown link
// @version      2024-09-07
// @description  Copy text with current URL link for MarkDown
// @author       koudyt
// @icon64       data:image/svg+xml;base64,
// @match        *://*/*
// @grant        GM_registerMenuCommand
// @grant        GM_setClipboard
// ==/UserScript==


GM_registerMenuCommand('Copy for github', function() {
    let selectedText = window.getSelection().toString();
    if (!selectedText) {
        console.log("Text couldn't be copied!");
        return;
    }
    let url = window.location.href;
    let finalText = `[${selectedText}](${url})`;

    // Tampermonkey clipboard API (cross-browser)
    GM_setClipboard(finalText);
    console.log('Text copied using GM_setClipboard');
});

function copyToClipboard(text) {
    navigator.clipboard.writeText(text).then(function() {
        console.log('Text copied');
    }, function(err) {
        console.error('Text copy error: ', err);
    });
}
